class distanceDTW{
  public:
    float distanceArray[numberPerGesture][accAxis];
    void printDistanceArray();
};

void distanceDTW::printDistanceArray() {
  Serial.println("distanceArray = {");
  for(int i = 0; i < numberPerGesture; i++) {
    Serial.print("{");
    for(int j = 0; j < accAxis; j++)  {
      Serial.print(String(distanceArray[i][j])+" ");
    }
    Serial.print("},");
    Serial.println("");
  }
  Serial.println("}");
}
  
float dtw_distance(float s1[s1_length], float s2[s2_length]) {
  float dtw[s1_length+1][s2_length+1] = {0.0};

  // Fill the first row and column with infinity
  for (int i = 1; i <= s1_length; i++) {
      dtw[i][0] = INFINITY;
  }
  for (int j = 1; j <= s2_length; j++) {
      dtw[0][j] = INFINITY;
  }
  dtw[0][0] = 0.0;

    // Fill in the rest of the DTW matrix
  for (int i = 1; i <= s1_length; i++) {
    for (int j = 1; j <= s2_length; j++) {
      float cost = abs(s1[i-1] - s2[j-1]); // Manhattan distance
      dtw[i][j] = cost + min(dtw[i-1][j], min(dtw[i][j-1], dtw[i-1][j-1]));
    }
  }

  // Return the DTW distance
  return dtw[s1_length][s2_length];
}